CREATE SEQUENCE MUDUR_ID_SEQ INCREMENT 1 START 1 CACHE 1;
CREATE TABLE MUDUR
(
ID				SMALLINT						NOT NULL		DEFAULT NEXTVAL('MUDUR_ID_SEQ'),
ISLEM_ZAMAN		TIMESTAMP WITHOUT TIME ZONE		NOT NULL,

AD				CHARACTER VARYING(50)			NOT NULL,
SOYAD			CHARACTER VARYING(50)			NOT NULL,

CONSTRAINT PK_MUDUR PRIMARY KEY (ID)
);

CREATE SEQUENCE OKUL_ID_SEQ INCREMENT 1 START 1 CACHE 1;
CREATE TABLE OKUL
(
ID				SMALLINT						NOT NULL		DEFAULT NEXTVAL('OKUL_ID_SEQ'),
ISLEM_ZAMAN		TIMESTAMP WITHOUT TIME ZONE		NOT NULL,

AD				CHARACTER VARYING(100)			NOT NULL,
OKUL_TIPI		CHARACTER VARYING(25)			NOT NULL,

CONSTRAINT PK_OKUL PRIMARY KEY (ID)
);

CREATE SEQUENCE OGRETMEN_ID_SEQ INCREMENT 1 START 1 CACHE 1;
CREATE TABLE OGRETMEN
(
ID				SMALLINT						NOT NULL		DEFAULT NEXTVAL('OGRETMEN_ID_SEQ'),
ISLEM_ZAMAN		TIMESTAMP WITHOUT TIME ZONE		NOT NULL,

AD				CHARACTER VARYING(50)			NOT NULL,
SOYAD			CHARACTER VARYING(50)			NOT NULL,
DERS			CHARACTER VARYING(20)			NOT NULL,
OKUL_ID			SMALLINT						NOT NULL,

CONSTRAINT PK_OGRETMEN PRIMARY KEY (ID),
CONSTRAINT FK_OGRETMEN_OKUL FOREIGN KEY (OKUL_ID) REFERENCES OKUL (ID)
);

CREATE SEQUENCE OGRENCI_ID_SEQ INCREMENT 1 START 1 CACHE 1;
CREATE TABLE OGRENCI
(
ID				SMALLINT						NOT NULL		DEFAULT NEXTVAL('OGRENCI_ID_SEQ'),
ISLEM_ZAMAN		TIMESTAMP WITHOUT TIME ZONE		NOT NULL,

AD				CHARACTER VARYING(50)			NOT NULL,
SOYAD			CHARACTER VARYING(50)			NOT NULL,
NUMARA			SMALLINT						NOT NULL,
SINIF			SMALLINT						NOT NULL,
MUDUR_ID		SMALLINT						NOT NULL,
OGRETMEN_ID		SMALLINT						NOT NULL,

CONSTRAINT PK_OGRENCI PRIMARY KEY (ID),
CONSTRAINT FK_OGRENCI_OKUL FOREIGN KEY (MUDUR_ID) REFERENCES MUDUR (ID),
CONSTRAINT FK_OGRENCI_OGRETMEN FOREIGN KEY (OGRETMEN_ID) REFERENCES OGRETMEN (ID)
);